import React, { useEffect, useState } from 'react';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [userId, setUserId] = useState(localStorage.getItem('userId') || null);
  const [token, setToken] = useState(localStorage.getItem('token') || null);
  const [isSignup, setIsSignup] = useState(false); // State to toggle between login and signup

  const fetchProfile = async () => {
    if (!userId || !token) return;

    try {
      const response = await fetch(`http://localhost:5000/api/users/${userId}/profile`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setProfile(data);
      } else {
        console.error('Error fetching profile');
        setProfile(null);
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
      setProfile(null);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, [userId, token]);

  const handleLoginSuccess = (userId, token) => {
    setUserId(userId);
    setToken(token);
    localStorage.setItem('userId', userId);
    localStorage.setItem('token', token);
    fetchProfile();
  };

  const handleSignupSuccess = (userId, token) => {
    setUserId(userId);
    setToken(token);
    localStorage.setItem('userId', userId);
    localStorage.setItem('token', token);
    fetchProfile();
  };

  const toggleForm = () => {
    setIsSignup(!isSignup);
  };

  if (!token) {
    return (
      <div>
        {isSignup ? (
          <SignupForm onSignupSuccess={handleSignupSuccess} />
        ) : (
          <LoginForm onLoginSuccess={handleLoginSuccess} />
        )}
        <button onClick={toggleForm}>
          {isSignup ? 'Already have an account? Login' : 'Need an account? Signup'}
        </button>
      </div>
    );
  }

  if (!profile) return <div>Loading...</div>;

  return (
    <div>
      <h2>Profile</h2>
      <p>Total Watch Time: {profile.totalWatchTime} seconds</p>
      <h3>Badges</h3>
      <ul>
        {profile.badges.map((badge, index) => (
          <li key={index}>{badge}</li>
        ))}
      </ul>
      <h3>Quiz Scores</h3>
      <ul>
        {profile.quizScores.map((quiz, index) => (
          <li key={index}>{quiz.videoId}: {quiz.score}</li>
        ))}
      </ul>
    </div>
  );
};

export default Profile;
