import React, { useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';

const VideoPlayer = () => {
  const { videoId } = useParams();
  const location = useLocation();
  const [feedback, setFeedback] = useState('');
  const [submitted, setSubmitted] = useState(false);

  // Extract video title and description passed as state from GettingStarted
  const { title, description } = location.state || { title: '', description: '' };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    // Here you can handle the submission, e.g., save feedback to a server or display it
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Video Player</h2>
      <div style={styles.videoSection}>
        <div style={styles.videoWrapper}>
        <iframe
  width="100%"
  height="400"
  src={`https://www.youtube.com/embed/${videoId}`}
  title="YouTube video player"
  frameBorder="0"
  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
  allowFullScreen
></iframe>
        </div>
        <div style={styles.videoDetails}>
          <h3 style={styles.videoTitle}>{title}</h3>
          <p style={styles.videoDescription}>{description}</p>
        </div>
      </div>

      <div style={styles.feedbackSection}>
        <h3 style={styles.subHeading}>Feedback Form</h3>
        {submitted ? (
          <p style={styles.thankYouMessage}>Thank you for your feedback!</p>
        ) : (
          <form onSubmit={handleFeedbackSubmit} style={styles.feedbackForm}>
            <textarea
              placeholder="Enter your feedback here..."
              value={feedback}
              onChange={(e) => setFeedback(e.target.value)}
              style={styles.textArea}
              required
            />
            <button type="submit" style={styles.submitButton}>Submit Feedback</button>
          </form>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif'
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center',
    color: '#333'
  },
  videoSection: {
    display: 'flex',
    flexDirection: 'row',
    gap: '20px',
    marginBottom: '30px'
  },
  videoWrapper: {
    flex: '2',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  videoPlayer: {
    borderRadius: '8px'
  },
  videoDetails: {
    flex: '1',
    padding: '10px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    border: '1px solid #ddd'
  },
  videoTitle: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#555',
    marginBottom: '10px'
  },
  videoDescription: {
    fontSize: '14px',
    color: '#777'
  },
  feedbackSection: {
    marginTop: '20px'
  },
  subHeading: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#333',
    marginBottom: '10px'
  },
  thankYouMessage: {
    color: '#28a745',
    fontSize: '16px'
  },
  feedbackForm: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px'
  },
  textArea: {
    width: '100%',
    height: '100px',
    padding: '10px',
    borderRadius: '8px',
    border: '1px solid #ddd',
    fontSize: '14px',
    fontFamily: 'Arial, sans-serif'
  },
  submitButton: {
    padding: '10px 20px',
    fontSize: '16px',
    fontWeight: 'bold',
    color: '#fff',
    backgroundColor: '#007bff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer'
  }
};

export default VideoPlayer;
