import React, { useEffect, useState } from 'react';
//import { useParams, useLocation } from 'react-router-dom';

const VideoPlayer = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [feedback, setFeedback] = useState('');
  const [submitted, setSubmitted] = useState(false);

  // Fetch the list of videos from the backend
  useEffect(() => {
    fetch('http://localhost:5000/api/videos')
      .then((response) => response.json())
      .then((data) => setVideos(data))
      .catch((error) => console.error('Error fetching videos:', error));
  }, []);

  const handleVideoSelect = (video) => {
    setSelectedVideo(video);
    setSubmitted(false);
    setFeedback('');
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    if (selectedVideo) {
      setSubmitted(true);
      fetch('http://localhost:5000/api/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          videoId: selectedVideo._id,
          feedback,
        }),
      })
        .then((response) => response.json())
        .then((data) => {
          console.log('Feedback submitted:', data);
          setFeedback('');
        })
        .catch((error) => console.error('Error submitting feedback:', error));
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Video Tutorials</h2>

      <div style={styles.videoList}>
        <h3 style={styles.subHeading}>Video List</h3>
        <ul>
          {videos.map((video) => (
            <li key={video._id} style={styles.videoItem} onClick={() => handleVideoSelect(video)}>
              <h4>{video.title}</h4>
              <p>{video.description}</p>
            </li>
          ))}
        </ul>
      </div>

      {selectedVideo && (
        <div style={styles.videoSection}>
          <div style={styles.videoWrapper}>
            <iframe
              width="100%"
              height="400"
              src={`https://www.youtube.com/embed/${selectedVideo.videoId}`}
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              style={styles.videoPlayer}
            ></iframe>
          </div>
          <div style={styles.videoDetails}>
            <h3 style={styles.videoTitle}>{selectedVideo.title}</h3>
            <p style={styles.videoDescription}>{selectedVideo.description}</p>
          </div>
        </div>
      )}

      {selectedVideo && (
        <div style={styles.feedbackSection}>
          <h3 style={styles.subHeading}>Feedback Form</h3>
          {submitted ? (
            <p style={styles.thankYouMessage}>Thank you for your feedback!</p>
          ) : (
            <form onSubmit={handleFeedbackSubmit} style={styles.feedbackForm}>
              <textarea
                placeholder="Enter your feedback here..."
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
                style={styles.textArea}
                required
              />
              <button type="submit" style={styles.submitButton}>
                Submit Feedback
              </button>
            </form>
          )}
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center',
    color: '#333',
  },
  videoList: {
    marginBottom: '30px',
  },
  subHeading: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#333',
    marginBottom: '10px',
  },
  videoItem: {
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    cursor: 'pointer',
    marginBottom: '10px',
  },
  videoSection: {
    marginBottom: '30px',
  },
  videoWrapper: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  videoPlayer: {
    borderRadius: '8px',
  },
  videoDetails: {
    padding: '10px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    border: '1px solid #ddd',
    marginTop: '20px',
  },
  videoTitle: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#555',
    marginBottom: '10px',
  },
  videoDescription: {
    fontSize: '14px',
    color: '#777',
  },
  feedbackSection: {
    marginTop: '20px',
  },
  thankYouMessage: {
    color: '#28a745',
    fontSize: '16px',
  },
  feedbackForm: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  textArea: {
    width: '100%',
    height: '100px',
    padding: '10px',
    borderRadius: '8px',
    border: '1px solid #ddd',
    fontSize: '14px',
    fontFamily: 'Arial, sans-serif',
  },
  submitButton: {
    padding: '10px 20px',
    fontSize: '16px',
    fontWeight: 'bold',
    color: '#fff',
    backgroundColor: '#007bff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
  },
};

export default VideoPlayer;
