import React from 'react';
import { Link } from 'react-router-dom';
import './GettingStarted.css'; // If you'd like to move styles to a CSS file

const GettingStarted = () => {
  const videos = [
    {
      title: 'Smartphone Basics',
      description: 'Learn the basics of using a smartphone, including settings, apps, and essential features.',
      videoId: 'example1' // replace with actual video ID
    },
    {
      title: 'Computer Basics',
      description: 'Understand the fundamentals of using a computer, including file management and system settings.',
      videoId: 'example2' // replace with actual video ID
    },
    {
      title: 'Internet Safety for Beginners',
      description: 'An introduction to safe internet practices for new users.',
      videoId: 'example3' // replace with actual video ID
    }
  ];

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Getting Started Videos</h2>
      <div style={styles.videoList}>
        {videos.map((video, index) => (
          <div key={index} style={styles.videoItem}>
            <Link
              to={{
                pathname: '/video-player',
                state: { title: video.title, videoId: video.videoId, description: video.description }
              }}
              style={styles.thumbnailLink}
            >
              <img
                src={`https://img.youtube.com/vi/${video.videoId}/hqdefault.jpg`}
                alt={video.title}
                style={styles.thumbnail}
              />
            </Link>
            <div style={styles.videoInfo}>
              <h3 style={styles.videoTitle}>{video.title}</h3>
              <p style={styles.videoDescription}>{video.description}</p>
              <Link
                to={{
                  pathname: '/video-player',
                  state: { title: video.title, videoId: video.videoId, description: video.description }
                }}
                style={styles.videoLink}
              >
                Watch Video
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif'
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center',
    color: '#333'
  },
  videoList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  videoItem: {
    display: 'flex',
    alignItems: 'flex-start',
    padding: '15px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9'
  },
  thumbnailLink: {
    marginRight: '15px',
    flexShrink: 0
  },
  thumbnail: {
    width: '120px',
    height: '90px',
    borderRadius: '8px'
  },
  videoInfo: {
    display: 'flex',
    flexDirection: 'column'
  },
  videoTitle: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#555'
  },
  videoDescription: {
    fontSize: '14px',
    color: '#777',
    marginBottom: '10px'
  },
  videoLink: {
    fontSize: '16px',
    color: '#1e90ff',
    textDecoration: 'none'
  }
};

export default GettingStarted;
